import useFetchData from '../hooks/fetchData';
import { useNavigate, useParams, Outlet } from 'react-router-dom';

import '../css/productPage.css';

import Image from '../elements/Image';
import Text from '../elements/Text';


const ProductPage = () => {

    const { data, error, loading } = useFetchData('products');

    const navigate = useNavigate();
    const { id } = useParams();

    console.log(id);

    const changeProduct = (e) => {
        navigate(e.target.id);
    };

    // if there was an error with getting the data it will be here
    if (error) {
        return (
            <div>
                there was an error
            </div>
        );
    }

    if (loading) {
        return (
            <div>
                THE PAGE IS LOADING
            </div>
        )
    }




    // This is the whole page
    return (
        <div className="productPage">
            {/* checks to see if an id is in the url, if there is it displays the product */}
            {id ? (
                <>
                    <button>Back</button>

                    <Outlet />
                
                </>

            ) : (
                // if there is no id, than it will display all the products as buttons
                data?.map((product) =>
                    <button key={product.id} id={product.id} onClick={(e) => { changeProduct(e) }} className="productPageButton">
                        <p>hi</p>
                        <p id={product.id}>{product.title}</p>
                    </button>
                )
            )}
        </div>
    );
}


export default ProductPage;
