import useFetchData from '../hooks/fetchData';
import { useParams, useNavigate } from 'react-router-dom';
// elements
import Box from '../elements/Box';
import Image from '../elements/Image';
import Text from '../elements/Text';
// styles
import '../css/productDescription.css';


const Product = () => {

    // this gets the id from the url, 
    const { id } = useParams();
    console.log(id)
    const { data, error, loading } = useFetchData('products/'+ id);

    const navigate = useNavigate();

    const backButton = () => {
        // the -1 goes back a page, or it goes back one dom item
        navigate(-1);
    };


    console.log(data)

    if(error){
        return(
            <div>
                there was an error
            </div>
        );
    }

    if(loading){
        return(
            <div>
                THE PAGE IS LOADING
            </div>
        )
    }
    return(
        <Box name="productDescriptionHolder">
            <Box name="imageHolder"><Image image={data.image} alt={data.title}></Image></Box>
            <Box name="descriptionHolder">
                <Text name="productText textMid">Name: {data.title}</Text>
                <Text name="productText textSmall">Price: ${data.price}</Text>
                <Text name="productText">{data.rating.rate}</Text>
                <Text name="productText">Catagory: {data.catagory}</Text>
                <Text name="productTextDescription">Description:<br></br> {data.description}</Text>
            </Box>
        </Box>
    );
}


export default Product;
