import useFetchData from '../hooks/fetchData';
import { useParams } from 'react-router-dom';
// elements
import Box from '../elements/Box';
import Image from '../elements/Image';
// styles
import '../css/productDescription.css';


const Product = () => {

    // this gets the id from the url, 
    const { id } = useParams();
    console.log(id)
    const { data, error, loading } = useFetchData('products/'+ id);


    console.log(data)

    if(error){
        return(
            <div>
                there was an error
            </div>
        );
    }

    if(loading){
        return(
            <div>
                THE PAGE IS LOADING
            </div>
        )
    }
    return(
        <>
        <div>Return button</div>
        <Box name="productDescriptionHolder">
            <Box name="imageHolder"><Image Image={data.image} alt={data.title}></Image></Box>
            <Box name="descriptionHolder">
                <p>{data.title}</p>
                <p>{data.price}</p>
                <p>{data.rating.rate}</p>
                <p>{data.description}</p>
                <p>{data.category}</p>
            </Box>
        </Box>
        </>
    );
}


export default Product;
