import useFetchData from '../hooks/fetchData';
import { useParams } from 'react-router-dom';


const Product = () => {

    // this gets the id from the url, 
    const { id } = useParams();
    
    const { data, error, loading } = useFetchData('products/'+ id);


    console.log(data)

    if(error){
        return(
            <div>
                there was an error
            </div>
        );
    }

    if(loading){
        return(
            <div>
                THE PAGE IS LOADING
            </div>
        )
    }
    return(
        <div>
            {data.id}
        </div>
    );
}


export default Product;
