import useFetchData from '../hooks/fetchData';
import { useParams } from 'react-router-dom';
// elements
import Box from '../elements/Box';
// styles
import '../css/productDescription.css';


const Product = () => {

    // this gets the id from the url, 
    const { id } = useParams();
    console.log(id)
    const { data, error, loading } = useFetchData('products/'+ id);


    console.log(data)

    if(error){
        return(
            <div>
                there was an error
            </div>
        );
    }

    if(loading){
        return(
            <div>
                THE PAGE IS LOADING
            </div>
        )
    }
    return(
        <Box className="productDescription">
            <Box><img src={data.image} alt={data.title}></img></Box>
            <Box>
                <p>{data.title}</p>
                <p>{data.price}</p>
                <p>{data.rating.rate}</p>
                <p>{data.description}</p>
                <p>{data.category}</p>
            </Box>
        </Box>
    );
}


export default Product;
