import useFetchData from '../hooks/fetchData';
import { useNavigate, useParams, Outlet } from 'react-router-dom';


const ProductPage = () => {

    const { data, error, loading } = useFetchData('products');

    const navigate = useNavigate();
    const { id } = useParams();

    console.log(id);

    const changeProduct = (e) => {
        navigate(e.target.id);
    };

    if (error) {
        return (
            <div>
                there was an error
            </div>
        );
    }

    if (loading) {
        return (
            <div>
                THE PAGE IS LOADING
            </div>
        )
    }




    // this is the display for the items, its the list
    return (
        <>

            <div>

            {id ? (
        <p>Viewing product ID: {id}</p>
      ) : (
        <p>Select a product.</p>
      )}


            </div>
            {data?.map((product) => {
                return (
                    <button key={product.id} id={product.id} onClick={(e) => { changeProduct(e) }}>
                        <p id={product.id}>{product.title}</p>
                    </button>
                );
            })}
            <Outlet />
        </>
    );
}


export default ProductPage;
