import useFetchData from '../hooks/fetchData';


const Product = () => {

    const { data, error, loading } = useFetchData('products/1');


    if (error) {
        return (
            <div>
                there was an error
            </div>
        );
    }

    if (loading) {
        return (
            <div>
                THE PAGE IS LOADING
            </div>
        )
    }

    // this is the display for the items, its the list
    return (
        <>
            {Array.isArray(data) && data.map(item => (
                <div key={item.id}>{item.name}</div>
            ))}
        </>
    );
}


export default Product;
