import useFetchData from '../hooks/fetchData';
import { useParams } from 'react-router-dom';


const Product = () => {

    const { id } = useParams();

    console.log(id);

    const { data, error, loading } = useFetchData('products/'+id);




    if(error){
        return(
            <div>
                there was an error
            </div>
        );
    }

    if(loading){
        return(
            <div>
                THE PAGE IS LOADING
            </div>
        )
    }

    // this is the display for the items, its the list
    return (
        <>
            {data?.map((product) => {
                return (
                    <div key={product.id}>
                        <p>{product.title}</p>
                        <p>{product.price}</p>

                    </div>
                );
            })}
        </>
    );
}


export default Product;
